class f{version;api;url;constructor(i){[this.version,this.api]=[i.version??1,i.api??"https://www.codewars.com/api"],this.url=`${this.api}/v${this.version}`}async request(i){return(await fetch(`${this.url}/${i}`)).json()}async user(i){return await this.request(`/users/${i}`)}async completedChallenges(i,d){if(!(Number.isInteger(d)&&d>-1))throw new TypeError("page must a positive integer!");return await this.request(`/users/${i}/code-challenges/completed?page=${d}`)}async authoredChallenges(i){return(await this.request(`/users/${i}/code-challenges/authored`)).data}async codeChallenge(i){return await this.request(`/code-challenges/${i}`)}}export{f as REST};
